        mounted() {
            this.technologies = _.shuffle(this.technologies)
            let moretech = _.cloneDeep(this.technologies)
            let moretech2 = _.cloneDeep(this.technologies)
            this.technologies = _.merge(this.technologies, moretech, moretech2)
            // this.featureTechnologies();
        },

        methods: {

            featureTechnologies() {

                this.resetActiveTechnologies()

                let total_technologies = this.technologies.length;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;
                this.technologies[Math.floor(Math.random()*total_technologies)].active = true;

                setTimeout(() => {
                    this.featureTechnologies()
                }, 2000)
            },

            resetActiveTechnologies() {
                this.technologies.forEach((tech) => {
                    tech.active = false
                })
            }

        }
